import * as React from 'react';
import { Link, RouteComponentProps } from 'react-router-dom';
import { connect } from 'react-redux';
import * as Authentication from '../../_reducers/Authantication';
import { ApplicationState } from "../../_reducers";

// At runtime, Redux will merge together...
type LoginProps =
    Authentication.AuthanticationState        // ... state we've requested from the Redux store
    & typeof Authentication.actionCreators      // ... plus action creators we've requested
    & RouteComponentProps<{}>;

type State = {
    username: string,
    password: string,
    submitted: boolean
}

class LoginPage extends React.Component<LoginProps, State> {
    constructor(props: any) {
        super(props);

        // reset login status
        this.props.logout();

        this.state = {
            username: '',
            password: '',
            submitted: false
        };

        this.handleOnChange = this.handleOnChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleOnChange(event: any): void {
        this.setState({ [event.target.name]: event.target.value });
    }

    handleSubmit(event: any): void {
        event.preventDefault();

        this.setState({ submitted: true });
        const { username, password } = this.state;
        if (username && password) {
            this.props.login(username, password);
        }
    }

    render() {
        const { loggingIn } = this.props;
        const { username, password, submitted } = this.state;
        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>Login</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <label htmlFor="username">Username</label>
                        <input type="text" className="form-control" name="username" value={username} onChange={e => this.handleOnChange(e)} />
                        {submitted && !username &&
                            <div className="help-block">Username is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={e => this.handleOnChange(e)} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary">Login</button>
                        {loggingIn &&
                            <img src="data:image/gif;base64," />
                        }
                        <Link to="/register" className="btn btn-link">Register</Link>
                    </div>
                </form>
            </div>
        );
    }
}

export default connect(
    (state: ApplicationState) => state.authantication, // Selects which state properties are merged into the component's props
    Authentication.actionCreators                 // Selects which action creators are merged into the component's props
)(LoginPage) as typeof LoginPage;
